INSERT INTO all_Characters ( name, alive, info, canon, pirate, marine, other, photo, devil_fruitID, pirate_groupID, marine_RanksID ) VALUES 

('AO', null , 'A O is an infamous New World pirate and captain of the A O Pirates. He and his crew were subordinates to Whitebeard. A O is a very tall man with a long, square shaped face, hollow eyes under prominent eyebrows, dark sideburns that go all the way down to his chin, and greenish long wavy hair. On his head, he wears a yellow and brown striped bandanna and over it is a tricorn captain''s hat with what appears to be his Jolly Roger on it, a white skull wearing an animal-like hood. He also wears a dark blue, open shirt with the sleeves rolled up and a ruffled collar, and elaborate earrings made up by three little jewels each on both ears. Not much is known about A O''s personality other than that he is very loyal to Whitebeard since he was willing to risk his own life to save Ace from execution instead of fleeing through the path he had cleared with his power. He was overjoyed when Luffy saved Ace, cheering with his fellow allies.',true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dd5k0rt-eb7d9729-7fff-40f3-bba0-22aa2c38f2b7.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..aYl4Dp4EM6p2fq_eq5cEEVp5-vz9AXevmGCN_IwK460', null, 36, null), 

('Abdullah', true, 'Abdullah is a criminal and a former bounty hunter who joined as a gladiator at the Corrida Colosseum to compete for the Mera Mera no Mi, along with his partner Jeet. He is an ally of Luffy during the Dressrosa Arc. After the battle with the Donquixote Pirates at Dressrosa, he joined the XXX Gym Martial Arts Alliance, which later converted to a pirate crew called the Ideo Pirates.',true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dfcr0aj-a0b93263-9eda-4bf3-9ce7-fee6ec56bf99.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..dvKjMRzwarOMqQYw9Ri2uGOJ9HClwBL0kYtQ0ufn4_g', null, 38, null),

('Absalom', false, 'Absalom of the Graveyard was the leader of the Zombie Soldiers and Zombie Generals of Thriller Bark prior to its collapse. He was one of the Mysterious Four of the Thriller Bark Pirates and one of the major antagonists of the Thriller Bark Saga. \n
Sometime during the timeskip, he became a reporter under the alias Absa. At some point, he was abducted by the Blackbeard Pirates and killed to poach his Devil Fruit powers, with his ability going to Shiryu.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dbcd5zf-7bb00f5f-f563-41fa-a013-462b3cb79a94.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..zQJe-JK9S1pk7SyfVwqpjIbLUP3z-8HgcTXPTn2hugw', 24, 9, null), 

('Acilia', true, 'Acilia is a gladiator at the Corrida Colosseum who participated in a tournament for the Mera Mera no Mi. She is a large and bulky woman. Has dark colored lipstick, and dark colored hair. She is seen with a jolly smile, and sports an armored helmet', true, false, false, 'gladiator', 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/1ce1372a-b75c-4058-b0c8-1bf1c2d6837e/de6g611-0b51a215-426f-4276-af1f-defc2f97c941.png/v1/fill/w_818,h_977,strp/acilia_by_hobbj_de6g611-pre.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..Myv9LN1hhs13j8UX3Pt5p6EnG-r8oM2pqtExJ7fjyHs', null, null, null),

('Adele', true, 'Adele is a mermaid and an employee at the Mermaid Caf√©. She is a youthful mermaid with beautiful curly hair that reaches past her shoulders, and has dark skin and pink lipstick. She wears a bikini top with a leopard skin pattern, and her tail is red in color with several zig-zag patterns all of which is being different hues of red themselves.', true, false, false, 'mermaid', null, null, null, null), 

('Aggie 68', true, 'Aggie 68 is a member of the Spade Pirates. Aggie 68''s abilities are unknown, but he and his crew were easily defeated by Newgate. He has been seen with a revolver-like gun attached to his wrist, but it is unknown how skilled he is in using it.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/8105260c-cfc1-463e-b35b-5de2d500fac9/dei3ncs-7f75b0b3-b23a-4926-ab1c-ddd2fadc2f69.png/v1/fill/w_1280,h_1368,strp/aggie_68___spade_pirate_crew_by_caiquenadal_dei3ncs-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..jQJZ-Gk9Jya7IX3yI5OGhqapZp1swzQWqhmxjVtgFTM', null, 60, null),

('Agotogi', true, 'Agotogi was the leader of a gang of bandits who tried to kidnap Vivi about 11 years before the princess''s return to Arabasta.', true, false, false, 'bandit', 'https://pbs.twimg.com/media/D9DqzWzXUAEV4LO?format=png&name=900x900', null, null, null),

('Agsilly', true, 'Agsilly is a New World pirate who allied himself with the Whitebeard Pirates. He seemed to be loyal to his alliance with the Whitebeard Pirates, willing to fight in the war at Marineford in order to rescue Ace from execution, but his full power has not yet been shown.', true, true, false, null, null, null, 48, null),

('Agyo', true, 'Agyo is a fighting lion and a gladiator competing at the Corrida Colosseum for the Mera Mera no Mi.', true, false, false, 'animal', null, null, null, null),

('Ahho Desunen IX', true, 'Ahho Desunen IX is a noble from the Goa Kingdom and the father of Ahho Zurako. Typical for a corrupted noble, Desunen is cruel and self-centered, as he believes that those who are not nobles are trash', true, false, false, 'noble', 'https://www.nautiljon.com/images/perso/00/63/ahho_desunen_ix_18336.webp', null, null, null),

('Ahho Zurako', true, 'Ahho Zurako is a noble from the Goa Kingdom and the daughter of Ahho Desunen IX. As a noble raised by a corrupted and spoiled parent, Zurako considers all people who are not nobles trash. She states that being burnt is their fault for not being born nobles.', true, false, false, 'noble', 'https://www.nautiljon.com/images/perso/00/43/ahho_zurako_18334.webp', null, null, null),

('Ahiru', true, 'Ahiru is the Deputy Commander of the East Army in the Revolutionary Army and the assistant of Belo Betty. Ahiru is a woman with tanned skin, prominent lips and long brown hair. Her left arm is a mechanical prosthesis with light and dark green (gray in the manga) coloring. She wears a black shirt, a red tie and a green jacket. She also has a pair of goggles on her head.', true, false, false, 'revolutionary', null, null, null, null),

('Aisa', true, 'Aisa is a young Shandia girl who used to sneak onto the Upper Yard frequently to collect Vearth. She is Isa''s daughter. Ever since she was born, she had the ability known as "Mantra". Because of this, she hates it when people die and will try anything to stop it, even rushing into danger without any plans on what to do. Aisa wants to be a strong fighter like everyone else and was originally afraid of Wyper, though she warmed up to him eventually. Aisa is also a bit of a tomboy as she reacted with disgust when Raki suggested she grow her hair longer because it would look cute on her.', true, false, false, 'shandorian', 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/1ce1372a-b75c-4058-b0c8-1bf1c2d6837e/de6sztl-eadafb80-daae-4cf6-a7d8-c7420b5a39e6.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..h3MlxExZKRDBbKTqMEAG2j_a6I_Y2B_3JaCquT0laO0', null, null, null),

('Akehende', true, 'Akehende is a Marine Rear Admiral. Like many other Marines, he considers pirates as nuisances to be eliminated.', true, false, true, null, 'https://api.duniagames.co.id/api/content/upload/file/254093731591020258.jpg', null, null, 4),

('Akudai Kanzaburo', true, 'Akudai Kanzaburo is an arms dealer who lives in the Flower Capital of Wano Country. Kanzaburo''s skills as an arms dealer effectively earned him an invite to the shogun''s banquet.', true, false, false, 'wano civilian', null, null, null, 4),

('Akumai', true, 'Akumai was a Billions agent who followed Mr. 2 Bon Kurei everywhere before he was captured by Hina when Mr. 2 and his crew sacrificed themselves to save the Straw Hat Pirates. Not much of Akumai''s skills or power is ever shown. His only fights are unseen for the most part. His current fate is unknown.', true, false, false, 'billions agent', 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/1ce1372a-b75c-4058-b0c8-1bf1c2d6837e/de3oggp-3e0a96d3-16ee-46bc-81d5-90101ac6c603.png/v1/fill/w_473,h_925,strp/akumai_by_hobbj_de3oggp-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.._NhFLBouPNQOgEBsdSlwJ7MMBnxgoVPORn9UBr1KyCU', null, null, null), 

('Aladine', true, 'Aladine is a goatsbeard brotula merman. He was originally a Ryugu Kingdom soldier who was then enslaved, but then became a pirate as the doctor and later first mate of the Sun Pirates. He is the husband of Charlotte Praline, serving as the bond between the Sun Pirates and the Charlotte Family.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dfcr0aa-a4dc6048-5d67-44c0-98d6-f92adb1b3b5a.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..S0xPPum64F_6R83_G6wbn-J0TqKNjBLaWDOX1Gjvdz0', null, 34, null),

 ('Albion', true, '"Gashed" Albion is one of the rookie pirates who joined the Fake Straw Hat Crew. He has a bounty of 92,000,000 beli''s. Not much is known about his abilities. He has at least four bladed weapons, so it might be assumed that he is a swordsman. He is seen holding three daggers between the fingers on his right hand and using them like claws, similar to Buggy''s knife-combat style. It can be assumed that he is rather strong since he was able to make it all the way to Sabaody Archipelago and almost became a Supernova. He was also able to escape Sentomaru''s platoon, when most of the other recruits were apprehended.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dbcd71j-bcb30839-3226-4fb7-b429-940a32b18036.png/v1/fill/w_640,h_512,strp/albion_by_bodskih_dbcd71j-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..RIIV-cZ9o5LSg1D76s4K_z89xDve99GGx5GHVlcZ5kA', null, 34, null),

('Ally', true, 'Ally is a little girl who was being held captive in the Biscuit Room, on Punk Hazard. She was freed with the rest of the children by the Straw Hat Pirates.', true, false, false, 'punk hazard child', 'https://cdn.anime-planet.com/characters/primary/ally-one-piece-1-190x285.jpg?t=1625995172', null, null, null), 

('Alpacaman', true, 'Alpacaman is a Gifter in the Beasts Pirates who serves as a guard in the Prisoner Mines of the Udon Region of Wano Country. He is shown using two sabers during his fight with Hyogoro and Luffy. He possesses some skill with them, and has a habit of spinning them around to show off and attack. He calls this style Alpaca Kenpo.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/1ce1372a-b75c-4058-b0c8-1bf1c2d6837e/de8b8fu-d369a8d6-3a13-4edb-a66a-38b15d8f8fd5.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..oJkmwTJB7rTh8GMUIw0dm-hTRLVnSBI8ZOsy8EPwayI', 131, 59, null),

('Alvida', true, '"Iron Mace" Alvida is the former captain of the Alvida Pirates and the first antagonist encountered by Luffy on his journey after going to sea. Due to her actions, she can be regarded as the secondary antagonist of the Romance Dawn Arc and the tertiary antagonist of the Loguetown Arc.
After her defeat against Luffy, she allies with Buggy and becomes one of the two captains of the Buggy and Alvida Alliance. She was the acting captain of the crew during Buggy''s time in Impel Down and during the Summit War of Marineford. After reuniting with Buggy, his army of Impel Down Convicts, and Galdino (and with the addition of Buggy becoming a Warlord of the Sea), her alliance with him has evolved into the Underworld Organization known as Buggy''s Delivery, and once again when that organization was re-integrated into the Organization known as Cross Guild.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dbcd0sj-ce9bee5a-3024-4746-b3d9-99b59a2947d8.png/v1/fill/w_640,h_512,strp/iron_mace_alvida_of_buggy_pirates_by_bodskih_dbcd0sj-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..4K_OW9M_FzdWc20w0iqfyQDWv3BwfVQLTtjQX_-EPYc', 3, 46, null),

('Amadob', true, 'Amadob is an infamous New World pirate who allied himself with Whitebeard. Amadob is a fearsome pirate who has managed to achieve an infamous reputation in the New World, and to make an alliance with Whitebeard. It is still unknown what abilities and weapons he possesses.', true, true, false, null, 'https://api.duniagames.co.id/api/content/upload/file/1080107921586254254.jpg', 3, 48, null),

('Amazon', true, 'Amazon was the elderly gatekeeper of Skypiea and informer at the entrance to Skypiea known as Heaven''s Gate. She let people in even if they could not pay her extol toll, but purposely failed to tell those who did not pay about the consequences for entering the city.
After the timeskip, she became the admission ticket saleswoman for Skypiea''s new theme park, Wagomuland.', true, false, false, 'shandorian', 'https://pbs.twimg.com/media/EBHdtbJX4AAYJLH?format=jpg&name=small', 3, 48, null),

('An Zengaiina', true, 'An Zengaiina is a prisoner on Level 2 of Impel Down. Not much is known about Zengaiina''s abilities, however he is dangerous enough to be imprisoned in the great prison although only on the second least secure level.', true, false, false, 'prisoner', null, 3, 48, null),

('Andre', true, 'Andre is an infamous New World pirate captain who was once a member of the Whitebeard Pirates, before forming his own crew subordinate to Whitebeard. He is the twin brother of the Marine officer Candre. ', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/1ce1372a-b75c-4058-b0c8-1bf1c2d6837e/de3b0wq-1f3a0095-09f2-498e-8140-3bfda5845016.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..dbazRRzH73ZY3rLSadzIzxftJ2s3ndoZ7C2717m-eSU', 3, 48, null),

('Anjo', true, 'Anjo is a pirate admirer. He witnessed Gol D. Roger''s execution. He has a tattoo of a plain Jolly Roger on his right arm.', true, false, false, 'civilian', null, null, null, null),

('Ankoro', true, 'Ankoro is a gigantic anglerfish formerly under the command of captain Vander Decken IX, residing in the Underworld of the Sea, seven thousand meters underneath the ocean surface. Wadatsumi seems to regard Ankoro as a pet, as apparently Ankoro was told "not to eat ships" before.', true, false, false, 'animal', null, null, null, null),

('Antonio', true, 'Antonio is the owner of "Antonio''s Graman" in Sabaody Archipelago. Chopper, Luffy, and Brook "sampled" multiple boxes of his food, causing him to ask if they would actually buy anything. However, they ended up buying a number of boxes along with some souvenirs.', true, false, false, 'sabaody civilian', 'https://cdn.anime-planet.com/characters/primary/antonio-one-piece-1-190x266.jpg?t=1626018973', null, null, null),

('Aphelandra', true, 'Aphelandra is a member of the tribe of Kuja living on the island of Amazon Lily. Despite her size, she has shown to be rather timid, often repeating what others have said to her as she obeys their command. She is a kind and innocent woman. Aphelandra is most likely a melee-type fighter where her prodigious size affords her a tremendous advantage over many foes. During the timeskip, she became a member of the Kuja Pirates.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dbcd2pf-57b053a5-1aab-45a3-9166-4c97b70aa0e9.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..SS130t4KZi3_PcJCd0bYt3bpdl7St-iMNjgykw6xTDo', null, 44, null),

('Aramaki', true, 'Aramaki, better known by his alias Ryokugyu, is an admiral in the Marines. He attained his rank during the two-year timeskip, along with Fujitora, both filling the two admiral vacancies left by Kuzan, who had resigned from the Marines, and Sakazuki, who had been promoted to fleet admiral.
Ryokugyu was first mentioned by Donquixote Doflamingo in the Dressrosa Arc, and debuted in the Levely Arc. His full appearance and real name were revealed near the end of the Wano Country Arc, in which he serves as a supporting antagonist.', true, false, true, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/b574e24b-607c-4a4d-b81d-40f05cf97d11/df7fbcg-4d9c04d6-5026-4ae4-b0a1-ed243f31c8ac.png/v1/fill/w_1024,h_1449,strp/aramaki_ryokugyu___green_bull_by_robirh_df7fbcg-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..nyRplfl_0cUfW-gHSGaSG6FFafSP8Piac29UPzFUBOY', 173, null, 2),

('Aremo Ganmi', true, 'Aremo Ganmi is a spectator at the Corrida Colosseum. She lives in Primula, a town in the west of Dressrosa.', true, false, false, 'dressrosa civilian', null, null, null, null),

('Arlong', true, 'Arlong the Saw is a sawshark fish-man. He was the captain of the all fish-man crew (excluding Nami), the Arlong Pirates, a former member of the Sun Pirates, and the older half-brother of Madam Shyarly. He is also a major inspirational figure for the New Fish-Man Pirates.
Following the death of his former captain, Fisher Tiger, Arlong was captured and sent to Impel Down, but received a pardon thanks to his comrade Jinbe becoming one of the Seven Warlords of the Sea. Once freed, Arlong set his sights on conquering the East Blue, settling himself on the Conomi Islands alongside his crew and racketeering the local population. He is also the man responsible for killing Bell-m√®re, Nami and Nojiko''s foster mother. Arlong then had Nami become his subordinate, taking advantage of her cartography skills to one day conquer all of the Blue Sea. He had the highest bounty in the East Blue before being defeated by Monkey D. Luffy. Due to his actions and role, he is the primary antagonist of the Arlong Park Arc.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dcv4cel-1b19d3e1-3b83-47f8-b521-36979b814a2e.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..9l_PjS2Rouu920tZAe5aVtB02nchgsjrRKHFGz9zqCU', null, 56, null),

('Arrow', false, 'He is tall and thin, with a pointed chin, with face paint around his eyes and around his jawline. He also wears shabby pants. He wields a double-edged sword with a golden hilt. Arrow is one of the four Tsumegeri Guards who drank the Hero Water and attempted to take down Crocodile, but failed and died from the water''s side effects shortly after Crocodile refused to even fight them to give them an honorable death', true, false, false, 'alabastian', null, null, null, null),

('Brahm', false, 'He is tall, with a hunchbacked appearance. He wears a turban and has light brown hair that covers his eyes. He wields two pole-arms. Brahm is one of the four Tsumegeri Guards who drank the Hero Water and attempted to take down Crocodile, but failed and died from the water''s side effects shortly after Crocodile refused to even fight them to give them an honorable death', true, false, false, 'alabastian', null, null, null, null),

('Barrel', false, 'He is bulky and rotund, and has a prominent nose, wearing thick white ropes around his shoulders, and has face paint around and under his eyes. He wields a large axe with a handle that is reminiscent of a flute. Barrel is one of the four Tsumegeri Guards who drank the Hero Water and attempted to take down Crocodile, but failed and died from the water''s side effects shortly after Crocodile refused to even fight them to give them an honorable death', true, false, false, 'alabastian', null, null, null, null),

('Hyota', false, 'He is very muscular, with a very large stubble-covered chin and a bulbous nose. He wields an extra large sword that resembles a butcher-knife. He is the leader of the group, under Chaka. Hyota is one of the four Tsumegeri Guards who drank the Hero Water and attempted to take down Crocodile, but failed and died from the water''s side effects shortly after Crocodile refused to even fight them to give them an honorable death.', true, false, false, 'alabastian', null, null, null, null),

('Aruyutayan V', false, 'Aruyutayan V was the king of Lvneel Kingdom roughly 400 years ago when Mont Blanc Noland discovered Shandora. Aruyutayan was a greedy and selfish king who was not above making rash decisions if it resulted in personal gains. He was ignorant to the challenges of seafaring, refusing to bring along Noland''s experienced crew in favor of his kingdom''s army, who otherwise had little experience at sea. Aruyutayan was also seemingly unfazed by losing most of his soldiers on the voyage, being driven primarily by his materialistic desires for Shandora.\n
When he was denied the city he so much craved for, Aruyutayan was quick to think Noland had tricked him in spite of the latter''s credibility as a hero and famous explorer. A major egocentric, Aruyutayan did not tolerate being made a fool of, using his authority as a royal to order not only Noland''s execution, but also to carry out a post-mortem smear campaign that would lead to Noland''s legacy being twisted into that of a goofy liar. Aruyutayan went as far as planting a fake witness to trick his subjects into despising the otherwise beloved Noland.', true, false, false, 'king', null, null, null, null),

('Asahija', true, 'Asahija is a Marine Chief Petty Officer whose shadow was once stolen by Gecko Moria.', true, false, true, null, null, null, null, 14),

('Asashichi', true, 'Asashichi is a resident of the Flower Capital who ran a soba shop with his wife. Asashichi cares deeply for his restaurant, as he was distraught when Page One destroyed it. He was angry at Sangoro for not intervening before his shop was destroyed.', true, false, false, 'wano civilian', null, null, null, null),

('Ashura Doji', false, 'Ashura Doji was a former bandit, a samurai from Wano Country and a retainer of the Kozuki Family, serving as one of Kozuki Oden''s Nine Red Scabbards. After Oden''s death, he returned to being a criminal under the name Shutenmaru, assuming leadership of the Mt. Atama Thieves. He initially severed his ties with the Kozuki Family in the present but rejoined after witnessing Yasuie''s death. \n
Ashura eventually met his demise during the Raid on Onigashima, sacrificing himself to protect his fellow Scabbards from one of Kanjuro''s schemes.', true, false, false, 'samurai', 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/ddqmght-4b1c2daf-64ee-4239-ba5d-551dad06313a.png/v1/fill/w_640,h_512,strp/ashura_doji_aka_shutenmaru_by_bodskih_ddqmght-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..0BNEhM303wXP9ECB15S6nsTf2uPG1vVuyX6glwL7TBY', null, null, null),

('Aswa', null, 'Aswa is Koza''s mother and Toto''s wife.', true, false, false, 'alabastian', null, null, null, null),

('Atagoyama', true, 'Atagoyama is a sumo wrestler in Wano Country''s Bakura Town.', true, false, false, 'sumo wrestler', null, null, null, null),

('Atlas', true, 'Atlas, also designated as Punk-05, is one of the six satellites of Dr. Vegapunk, embodying his aspect of "violence". Being designated the ‚Äúviolence‚Äù satellite of Vegapunk, Atlas has a short temper and is prone to violence upon getting angry, which has been shown to occur if someone attacks her or if people show an inability to utilize her inventions successfully. She is very talkative and will gladly give detailed explanations on the inner workings of her inventions. She is quite interested in the question of the definition of reality, and though she has a firm view on the matter, she thinks other people need to decide the answer for themselves.', true, false, false, 'vegapunk', 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/512bfbe4-1969-4c9a-9a06-ef57ffafe2e0/dflrmvo-ca9582d5-600d-4322-8506-261b3ee121bb.png/v1/fill/w_902,h_886,q_70,strp/vegapunk_satellites___atlas__by_corasaan_dflrmvo-pre.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..Ck5tiBFwep7xO_NFHEUx_EW-nu2s5FWsQusjAeEmOV8', null, null, null),

('Atmos', true, '"Water Buffalo" Atmos is the commander of the 13th division of the Whitebeard Pirates. Atmos carries around two large swords and can deal devastating blows with them, as seen when he attacked and destroyed the swords of some of his crewmates while Doflamingo controlled him. In the anime, he could even block bullets that were shot from rifles to protect his allies.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dbo8ktn-2493f5e3-3bb4-412d-847d-014d71689236.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..c_zKMPkeCY2i9TYWrAeLdbhZlXWNFt00EadQz7i6Te4', null, 48, null),

('Attach', true, 'Attach, commonly nicknamed Attachan, is a prolific photographer who once headed the Marines'' Photography Department. Though he supplied images of many prominent outlaws - including most of the Straw Hat Pirates - for the Marines'' wanted posters, his frequent blunders eventually led to his dismissal. Subsequently, he became a reporter for the World Economic Journal, under "Big News" Morgans.
Like Minatomo, Attach began as an SBS joke Eiichiro Oda used to "explain" a triviality - namely, how the Marines always had such high-quality illustrations of the world''s most dangerous and/or unreachable criminals. In time, however, he became a fully canon character, with minor but tangible impact on the story.', true, false, false, 'reporter', null, null, null, null),

('Avalo Pizarro', true, 'Avalo Pizarro, also known as the "Corrupt King", is one of the Ten Titanic Captains, and the captain of the Fourth Ship of the Blackbeard Pirates. He was one of the criminals who was given a life sentence in Level 6 of Impel Down, as his crimes were so atrocious he had to be effectively be erased from history. Prior to his life of crime, he was once a king of a North Blue kingdom that overthrew him for his despotic ways. He has since been freed by Marshall D. Teach and has become a member of the Blackbeard Pirates.
He is currently one of the Ten Titanic Captains, and the captain of the Fourth Ship of the Blackbeard Pirates.', true, true, false, null, 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/51158316-fd7e-48ca-b5fe-8542e9dfe357/dehdf0z-702052ee-be96-4344-83eb-9b7ba4928e18.png/v1/fill/w_640,h_512,strp/avalo_pizarro_of_blackbeard_pirates_by_bodskih_dehdf0z-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..Ob-F96NqDG3kcdWEII1-9kWcK93ADRXPAUMyPEw5cUY', null, 30, null),

