INSERT INTO devil_fruits ( org_name, eng_name, canon, movie, photo, descr, awakened, currentUser, previous_user, type_of_fruit ) VALUES
('Gomu Gomu no Mi', 'Gum-Gum Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/12/Gomu_Gomu_no_Mi_Infobox.png/revision/latest?cb=20190419140206', 'grants the user the properties of rubber, effectively turning them into a Rubber Human. It was originally known as the Hito Hito no Mi, Model: Nika and was classified as a Mythical Zoan-type fruit that allows the user to transform into the legendary "Sun God" Nika and gain his attributes before being reclassified by the World Government.', 'grants the user the properties of rubber, effectively turning them into a Rubber Human. It was originally known as the Hito Hito no Mi, Model: Nika and was classified as a Mythical Zoan-type fruit that allows the user to transform into the legendary "Sun God" Nika and gain his attributes before being reclassified by the World Government.', 'Monkey D. Luffy', null, 1),

('Bara Bara no Mi', 'Chop-Chop Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/9c/Bara_Bara_no_Mi_Infobox.png/revision/latest?cb=20201102125104', 'allows the user to split their own body into pieces and control said pieces however they wish, making the user a Splitting Human.', null, 'Buggy', null, 1),

('Sube Sube no Mi', 'Smooth-Smoth Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/e/e9/Sube_Sube_no_Mi_Infobox.png/revision/latest?cb=20200912191823', 'makes the user''s body smooth and slippery, which in turn makes most attacks and objects slide off their body, protecting the user from harm in most situations.', null, '''Iron Mace'' Alvida', null, 1),

('Bomu Bomu no Mi', 'Bomb-Bomb Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/1a/Bomu_Bomu_no_Mi_Infobox.png/revision/latest?cb=20210204030003', 'allows the user to make any part of their body explode, whether it be their limbs, hair, mucus, or even breath; making the user a Bomb Human.', null, 'Gem of the Border aka Mr. 5', null, 1), 

('Kiro Kiro no Mi', 'Kilo-Kilo Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/8/89/Kiro_Kiro_no_Mi_Infobox.png/revision/latest?cb=20210204030042', 'allows the user to change their weight from 1 to 10,000 kilograms without, in any way, affecting the overall size of their body', null, 'Mikita aka Miss Valentine', null, 1),

('Hana Hana no Mi','Flower-Flower Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/2/21/Hana_Hana_no_Mi_Infobox.png/revision/latest?cb=20191228120242', 'allows the user to replicate and sprout pieces of their body from the surface of any object or living thing.', null, 'Nico Robin aka Miss All Sunday', null, 1),

('Doru Doru no Mi', 'Wax-Wax Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/5/5f/Doru_Doru_no_Mi_Infobox.png/revision/latest?cb=20200507205151', 'allows the user to create large quantities of candle wax from their body and control it, making the user a Candle Human.', null, 'Galdino aka Mr. 3', null, 1),

('Baku Baku no Mi', 'Munch-Munch Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/c/c6/Baku_Baku_no_Mi_Infobox.png/revision/latest?cb=20200507163319', 'allows the user to eat anything. The eaten items can then be merged into and manifested from the user''s body or be combined to create something else.', null, '''Tin-Plate'' Wapol', null, 1),

('Mane Mane no Mi', 'Clone-Clone Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/0e/Bentham_Anime_Infobox.png/revision/latest/scale-to-width-down/281?cb=20160929070742', 'allows the user to turn into a physical double of anyone they have touched, making the user an Imitating Human (マネ人間 Mane Ningen?).[2] When the user changes, they also imitate the copied person''s flexibility, strength, and abilities, aside from those bestowed by a Devil Fruit. They can change to someone else by touching their face with their right hand and switch back to their normal state by touching their face with their left hand.', null, 'Bentham aka Mr. 2 Bon Kurei', 'Kurozumi Higurashi', 1),

('Supa Supa no Mi', 'Dice-Dice Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/f/f9/Supa_Supa_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160925122350', 'allows the user to transform any part of their body into a blade, thus gaining slashing abilities and making their skin as hard as steel.', null, 'Daz Bonez aka Mr.1', null, 1),

('Toge Toge no Mi', 'Spike-Spike Fruit', true, null,'https://static.wikia.nocookie.net/onepiece/images/e/ee/Toge_Toge_no_Mi_Infobox.png/revision/latest?cb=20130120203503', 'allows the user to grow strong pointy spikes from their body.', null, 'Zala aka Miss Doublefinger', null, 1),

('Ori Ori no Mi', 'Cage-Cage Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/7/7a/Ori_Ori_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130115015011', 'allows the user to bind their foes in iron shackles created by the user''s body.' , null, 'Hina', null, 1),

('Bane Bane no Mi', 'Spring-Spring Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/1d/Bane_Bane_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160913222919', 'allows the user to turn their limbs into springs, making the user a Spring Human.', null, 'Belamy', null, 1),

('Ito Ito no Mi', 'String-String Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/a/ae/Ito_Ito_no_Mi_Infobox.png/revision/latest/scale-to-width-down/700?cb=20151220070449', 'allows the user to create and manipulate strings, making the user a String Human. If awoken, the fruit''s power can allow the consumer to transmute the surrounding area, like the ground and buildings, into strings as well, which they can utilize for much stronger attacks and defense.', true, 'Donquixote Donflamingo', null, 1),

('Noro Noro no Mi', 'Slow-Slow Fruit', true, null,'https://static.wikia.nocookie.net/onepiece/images/7/7a/Noro_Noro_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130414195444', 'allows the user to emit special unique photons. Anything hit by these photons will be trapped in a state of slow motion for 30 seconds.', null, 'Foxy', null, 1),

('Doa Doa no Mi', 'Door-Door Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/04/Doa_Doa_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130629174737', 'allows the user to create doors through anything they touch, making the user a Door Human (ドア人間 Doa Ningen).The doors they create can be created from any part of their body, from the palm of their hands to their whole body itself, and usually take the shape and form of the body part creating it. The doors remain as passageways anyone can pass through until closed completely.', null, 'Blueno', null, 1),

('Awa Awa no Mi', 'Bubble-Bubble Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/5/51/Awa_Awa_no_Mi_Infobox.png/revision/latest/scale-to-width-down/700?cb=20131206044150', 'allows the user to emit and control soap and bubbles that can not only clean off dirty but can also ''clean off'' power, making the user a Soap Human.', null, 'Kalifa', null, 1),

('Beri Beri no Mi', 'Berry-Berry Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/2/27/Beri_Beri_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130503053935', 'allows the user to transform their body parts into a multitude of orbs or spheres shaped like the round parts of berries, making the user a Grape Human (ブドウ人間 Budō Ningen). The effect is similar to that of the Bara Bara no Mi.', null, 'Very Good', null, 1),

('Sabi Sabi no Mi', 'Rust-Rust Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/7/7c/Sabi_Sabi_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160708014101', 'allows the user to cause metallic items to rust and come apart, making the user a Rust Human (サビ人間 Sabi Ningen).', null, 'Shu', null, 1),

('Shari Shari no Mi', 'Wheel-Wheel Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/e/e4/Shari_Shari_no_Mi_Infobox.png/revision/latest?cb=20220723150343', 'allows the user to transform their arms and feet into wheels, making the user a Wheel Human (車輪人間 Sharin Ningen).', null, 'Sharinguru', null, 1),

('Horo Horo no Mi', 'Hollow-Hollow Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/14/Horo_Horo_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130922165024', 'allows the user to create and control ghosts with different abilities, such as inflicting feelings of despair on people whom they pass through, or exploding with great force. The user can also make their own ghost leave their body as a form of astral projection.', null, 'Perona', null, 1),

('Yomi Yomi no Mi', 'Revive-Revive Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/3/3b/Yomi_Yomi_no_Mi_Infobox.png/revision/latest?cb=20201116170233', 'enhances the user''s soul to the point where they resurrect after their first death, allowing them to live a second time and to use several other soul-based abilities, making the user a Reviving Human.', null, '''Soul King'' Brook', null, 1),

('Kage Kage no Mi', 'Shadow-Shadow Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/7/7b/Kage_Kage_no_Mi_Infobox.png/revision/latest?cb=20201116170246', 'allows the user to manifest and control shadows. They can animate and freely manipulate their own shadow, as well as steal and absorb other people''s shadows, making the user bigger and stronger. Stolen shadows can also be implanted into corpses, reanimating them.', null, 'Gecko Moria', null, 1), 

('Suke Suke no Mi', 'Clear-Clear Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/bb/Suke_Suke_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20200112035251', 'allows the user to become invisible, as well as make anything they touch invisible.', null, 'Shiryu', 'Absalom', 1),

('Nikyu Nikyu no Mi', 'Paw-Paw Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/e/e4/Nikyu_Nikyu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20190411220015', 'bestows its user the ability to repel everything they touch, and is physically represented as paws on the user''s palms, making the user a Paw Human. The paws are permanently engraved into the user, as Kuma is always seen wearing gloves when his powers are not in use.', null, 'Bartholomew Kuma', null, 1),

('Jiki Jiki no Mi', 'Magnet-Magnet Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/13/Jiki_Jiki_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20220525022232', 'allows the user to create magnetic forces and use them to control metal', null, 'Eustass Kid', null, 1),

('Ope Ope no Mi', 'Op-Op Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/0e/Ope_Ope_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20210409181034', 'grants the ability to manifest a spherical space in which the user can manipulate the orientation, movements, and physical configureation of anything and anyone (themselves included) in a ''surgical'' fashion, making the user a Free Modification Human', null, 'Trafalgar D. Water Law', null, 1),

('Shiro Shiro no Mi', 'Castle-Castle Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/e/e8/Shiro_Shiro_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160115065100', 'allows the user to become a living fortress equipped with cannons, as well as letting them shrink people and objects who enter their body and house them inside.', null, 'Capone Bege', null, 1),

('Wara Wara no Mi', 'Straw-Straw Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/b4/Wara_Wara_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180803070246', 'allows the user to create and control straw. They can also create straw voodoo dolls, allowing the user to transfer the damage they take to the dolls and, by extension, the people connected to them.', null, 'Basil Hawkins', null, 1),

('Oto Oto no Mi', 'Tone-Tone Frui', true, null, 'https://static.wikia.nocookie.net/onepiece/images/1/1e/Apoo_Using_Musical_Powers.png/revision/latest/scale-to-width-down/350?cb=20130209042848', 'By transforming their body parts into instruments (such as turning teeth into piano keys or an arm into a trumpet) and playing specific notes and tunes, the user can release music that causes damaging effects to their opponents, the type of damage being determined by what they play. There is a wide range of attacks the user can perform, from physical strikes like punches and slashes (which can dismember victims) to elemental attacks like explosions.', null, 'Scratchman Apoo', null, 1),

('Mero Mero no Mi', 'Love-Love Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/98/Mero_Mero_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180627192522', 'user is able to transform those who lust or love after the user into stone in varying degrees by using their ''dirty thoughts''. Victims that have been completely turned to stone are incapable of consciousness and susceptible to physical damage unless the user de-petrifies them. Petrification can also be achieved through physically striking an opponent, although the only area that is turned to stone is the contacted area, as opposed to the entire body. The power also extends to nonliving objects.', null, 'Boa Hancock', null, 1),

('Doku Doku no Mi', 'Venom-Venom Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/2/21/Doku_Doku_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160129102538', 'grants the user the ability to produce and control different types of poison, as well as grants immunity to all forms of poison, making the user a Poison Human (毒人間 Doku Ningen).', null, 'Magellan', null, 1), 

('Horu Horu no Mi', 'Horm-Horm Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/5/53/Horu_Horu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20120328040657', 'allows the user to create special hormones that can alter a person''s body in various ways, as well as turn their fingers into syringes that they can use to inject the hormones into a person''s body.', null, 'Emporio Ivankov', null, 1),

('Choki Choki no Mi', 'Shear-Shear Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/0f/Choki_Choki_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20130224140928', 'allows the user to transform parts of their body into scissors and cut solid objects as if they were paper, making the user a Scissors Human (ハサミ人間 Hasami Ningen).', null, 'Inazuma', null, 1),

('Gura Gura no Mi', 'Tremor-Tremor Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/e/e3/Gura_Gura_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20200716073745', 'the user is its ability to generate massive vibrations, or shockwaves, which can travel through virtually any medium, including the ground, the seafloor, and even the air.', null, 'Marshall D. Teach', 'Edward Newgate aka Whitebeard', 1),

('Kira Kira no Mi', 'Twinkle-Twinkle Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/a/ad/Kira_Kira_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20101021104815', 'allows the user to transform their body into diamond.', null, 'Jozu', null, 1),

('Poke Poke no Mi', 'Pocket-Pocket Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/2/26/Poke_Poke_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20220426093454', 'allows the user to store various things inside pockets in their body. The objects stored inside can be much larger than the opening of the pocket, as they change size accordingly. It is stated that the user can have infinite pockets in their person, effectively giving them limitless storage capacity.', null, 'Blamenco', null, 1), 

('Woshu Woshu no Mi', 'Wash-Wash Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/4/4c/Woshu_Woshu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20121228203008', 'allows the user to ''wash and hang out to dry'' people and objects as if they were clothes. This seems to render their victims unable to fight back or move.', null, 'Tsuru', null, 1),

('Fuwa Fuwa no Mi', 'Float-Float Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/d/df/Fuwa_Fuwa_no_Mi_Infobox.png/revision/latest/scale-to-width-down/1000?cb=20130323001748', 'allows the user to levitate themselves and non-living things into the air.', null, 'Shiki', null, 1),

('Mato Mato no Mi', 'Mark-Mark Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/b7/Mato_Mato_no_Mi_Infobox.png/revision/latest?cb=20220726190549', 'makes the user capable of aiming at whoever they touch with their hands. The user can also memorize as many targets with as many hands as they have.', null, 'Vander Decken IX', null, 1),

('Fuku Fuku no Mi', 'Garb-Grab Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/9c/Fuku_Fuku_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20181020010802', 'allows the user to create clothing and other apparel out of leaves or stones by visualizing them.', null,'Kin''emon', null, 1),

('Hobi Hobi no Mi', 'Hobby-Hobby Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/3/3d/Hobi_Hobi_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20190416040233', 'allows the user to transform living people into toys, and erase memories of their existence from others. It has a secondary side effect of granting the eater eternal youth.', null, 'Sugar', null, 1),

('Buki Buki no Mi', 'Arms-Arms Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/3/3e/Buki_Buki_no_Mi_Infobox.png/revision/latest?cb=20141206174322', 'gives the user the ability to change their body into any kind of weapon. The user can either transform small parts of their body, like the fingers, hands or arms; or a larger body-section, such as their entire torso or even their whole body, into a weapon.', null, 'Baby 5', null, 1),

('Guru Guru no Mi', 'Spin-Spin Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/0a/Guru_Guru_no_Mi_Infobox.png/revision/latest?cb=20141206170537', 'grants its eater the power to transform any part of their body or piece of worn clothing into propellers capable of rotation at high speed. The force of the spinning is strong enough to enable propelled flight by the user over very long distances, to assist in the carrying or thrown launch of objects and other people, and to create powerful windstorms.', null, 'Buffalo', null, 1),

('Beta Beta no Mi', 'Stick-Stick Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/d/d9/Beta_Beta_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180513215218', 'allows the user to create and control mucus, making the user a Mucus Human (粘液人間 Nen''eki Ningen).', null, 'Trebol', null, 1), 

('Zushi Zushi no Mi', 'Press-Press Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/ba/Zushi_Zushi_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160529113354', 'Whoever eats from this fruit is granted the power to create gravitational forces of extreme potency. The ability user is able to move about living things and objects with little effort, ranging from debris to send flying against an enemy in smaller or massive numbers, to a massive battleship. The range of the generable gravity field appears incredibly big, as it can pull down meteors from the highest of skies (possibly the outer atmosphere, or even space itself.', null, 'Issho aka Admiral Fujitora', null, 1),

('Bari Bari no Mi', 'Barrier-Barrier Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/d/de/Bari_Bari_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20160212174221', 'The fruit''s bestowed ability is the conjuration of physical barriers—either invisible or visibly transparent, resembling panes of glass—which is done by the user''s crossing their fingers. This protects the user from attacks from either front or back. The barriers have immense defensive power.', null, 'Bartolomeo', null, 1),

('Nui Nui no Mi', 'Stitch-Stitch Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/00/Nui_Nui_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20190526000026', 'allows the user to stitch objects to any surface. This makes it useful for restraining people, pinning them to the ground, to a wall or even other nearby individuals. The user is also able to undo the stitches of the object with ease, freeing it from their power.', null, 'Leo', null, 1),

('Giro Giro no Mi', 'Glare-Glare Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/2/23/Giro_Giro_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20201101164342', 'allows the user to see through everything, both literally and figuratively. By forming circles out of their index finger and thumb and putting their hands next to their eyes, like glasses, the user can see through a person''s clothes and skin, much like X-ray specs, but also read their minds and memories, allowing them to see through any lies. It also allows the user to transfer their own memories to another person''s mind, by letting another person look through the circled fingers.', null, 'Viola', null, 1),

('Ato Ato no Mi', 'Art-Art Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/0/0f/Ato_Ato_no_Mi_Infobox.png/revision/latest?cb=20160708134450', 'allows the user to transform any target into an abstract art, distorting it in both appearance and usage. This is accomplished by the user creating a thought cloud, and then tossing it at the target, causing it to distort. Any weapons or vehicles warped by this power loses its functionality; even natural weapons such as hooves and antlers are turned useless.', null, 'Giolla', null, 1),

('Jake Jake no Mi', 'Jacket-Jacket Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/c/ce/Kelly_Funk_Immediately_After_Transforming.png/revision/latest?cb=20140525204909', 'Allows the user to transform into a whole-body jacket and control the body of the person wearing them. While being worn, the user can take advantage of both their own physical abilities and those of the person wearing them.', null, 'Kelly Funk', null, 1),

('Pamu Pamu no Mi', 'Pop-Pop Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/a/a9/Pamu_Pamu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20141215214103', 'allows the user to cause anything inorganic they touch to swell up, which will then rupture and explode violently. The user can also apply it to themselves. The user remains unharmed from the explosion.', null, 'Gladius', null, 1),

('Sui Sui no Mi', 'Swim-Swim Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/9d/Sui_Sui_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20141019165710', 'allows the user to swim in solid matter such as ground and walls as if it were water, the latter even allowing the user to swim upwards, seemingly disregarding gravity. It also allows the user to submerge into the ground to avoid colliding with it upon falling from a great height by performing a high dive. The fruit can also allow the user to move discreetly, as they will be mostly covered by the ground, allowing them to infiltrate places more easily.', null, 'Señor Pink', null, 1),

('Ton Ton no Mi', 'Ton-Ton Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/4/40/Ton_Ton_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20151003022742', 'allows the user to increase their body weight by incredible amounts, leveled by metric tons. This allows them to land crushing attacks that can easily crack the ground and even a giant''s bones.', null, 'Machvise', null, 1),

('Hira Hira no Mi', 'Ripple-Ripple Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/5/5a/Hira_Hira_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20151110020239', 'allows the user to make their body and anything they touch gain the malleable properties of cloth fabric. As such, the affected materials become completely flexible, flat, and foldable; this effect is strong enough to let hardened metal flutter like a flag in the breeze at the user''s command. At the same time, all other natural characteristics such as toughness, strength, or sharpness are retained by the material, so metal objects can still resist forceful impact from enemies and blades retain an edge.', null, 'Diamante', null, 1),

('Ishi Ishi no Mi', 'Stone-Stone Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/90/Ishi_Ishi_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180529180105', 'allows the user to take control of anything made of stone that they come in contact with. While under the user''s control, the stone gains a surprising level of flexibility and dexterity that it otherwise would not have.', null, 'Pica', null, 1),

('Fude Fude no Mi', 'Brush-Brush Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/b4/Fude_Fude_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20210710161433', 'allows the user to generate ink from their body and use a painting brush to turn images created with that ink into three-dimensional, lifelike objects.', null, 'Kurozumi Kanjuro', null, 1),

('Nagi Nagi no Mi', 'Calm-Calm Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/3/37/Nagi_Nagi_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20150802120152', 'user of this Devil Fruit has the ability to create a soundproof field. The field makes it so that the sounds inside the field cannot be heard from the outside, and vice versa. The user can also use this fruit''s ability to cancel all noises coming from the user or somebody else''s actions, making it ideal for situations requiring stealth since the lack of noise helps the user prevent drawing attention from enemies while realizing tasks that, otherwise, would be hard to conceal.', null, 'Donquixote Rosinante', null, 1),

('Chiyu Chiyu no Mi', 'Heal-Heal Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/b/b2/Chiyu_Chiyu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20151129192854', 'allows the user to heal any living thing that is wounded or injured. The healing powers manifest in the form of tears from the user''s body. As soon as they touch a living thing, it is restored to perfect health and can even recover people suffering from a massive shock; however, despite being able to heal body parts back together, it cannot create them from nothing.', null, 'Mansherry', null, 1),

('Soru Soru no Mi', 'Soul-Soul Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/9/9d/Soru_Soru_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20170521101146', 'the fruit''s power of soul manipulation consists of manifesting and drawing out the souls of people who feel fear. The manifested souls take the form of a light-pink, spectral-looking but (for the user) tangible substance, which leaks out of the victim''s body like smoke. The user can extract the victim''s soul by grabbing it once it is surrounding the target''s body and pulling it out. This effectively steals from the victim''s very lifespan, the amount of which is determined by the user: it is possible to steal decades of a person''s life at once should they desire, or mere seconds. The user can steal part of the victim''s lifespan, leaving their remaining lifespan that much shorter, or even take enough years to steal a victim''s entire remaining lifespan, killing them instantly.', null, 'Charlotte Linlin aka Big Mom', 'Mother Carmel', 1),

('Mira Mira no Mi', 'Mirror-Mirror Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/c/c3/Mira_Mira_no_Mi_Infobox.png/revision/latest/scale-to-width-down/334?cb=20170709205701', 'allows the user to create multiple mirrors in their own vicinity with their own hands. These mirrors can be used as a shield against enemy attacks, absorbing them and reflecting them back at the attacker, turning their own power against them. These mirrors can also be used as portals into what appears to be a pocket dimension within the mirrors known as Mirro-World (鏡世界ミロワールド Miro Wārudo), with no apparent way back through, leaving their opponents trapped.', null, 'Charlotte Brûlée', null, 1),

('Pero Pero no Mi', 'Lick-Lick Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/7/77/Pero_Pero_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20190412202445', 'allows the user to create and control seemingly limitless amounts of candy, even being able to make it move as if it were a mechanical construct, like escalators and prostheses. The candy created by the user is edible. When hardened, the candy becomes incredibly strong and resilient.', null, 'Charlotte Perospero', null, 1), 

('Bisu Bisu no Mi', 'Bis-Bis Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/8/8b/Bisu_Bisu_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20170730022308', 'allows the user to generate and manipulate biscuits at will, making them a Biscuit Human (ビスケット人間 Bisuketto Ningen).', null, 'Charlotte Cracker', null, 1),

('Bata Bata no Mi', 'Butter-Butter Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/6/61/Bata_Bata_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20181203192223', 'allows the user to control seemingly limitless amounts of butter. Despite butter usually making objects slippery, the created butter has been shown to be quite sticky and adhesive when in semi-liquid form, capable of immobilizing an opponent by wrapping it around their hands or bodies', null, 'Charlotte Galette', null, 1), 

('Buku Buku no Mi', 'Book-Book Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/4/44/Buku_Buku_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180904134020', 'grants the user the ability to control nearby books, regardless of their size, allowing them to telekinetically move them around and even use them as footholds. The biggest strength of this fruit is the ability to trap living things inside a book''s setting.', null, 'Charlotte Mont-d''Or', null, 1),

('Kuri Kuri no Mi', 'Cream-Cream Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/7/73/Kuri_Kuri_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180913105124', 'allows the user to create and control seemingly limitless amounts of cream, which they can use to attack and capture their foes. The cream''s sugar is capable of inflicting painful second or third degree burns to anyone it comes in contact with, allowing the user to physically harm their opponents while also making them hard to approach in battle due to the risk of coming in contact with the cream.', null, 'Charlotte Opera', null, 1),

('Shibo Shibo no Mi', 'Wring-Wring Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/d/d7/Shibo_Shibo_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20180531213234', 'allows its user to drain liquid from both living and non-living things by wringing them, as well as absorb liquid into themselves and their weapons, making them a Dehydration Human (脱水人間 Dassui Ningen).', null, 'Charlotte Smoothie', null, 1),

('Memo Memo no Mi', 'Memo-Memo Fruit', true, null, 'https://static.wikia.nocookie.net/onepiece/images/c/c0/Memo_Memo_no_Mi_Infobox.png/revision/latest/scale-to-width-down/350?cb=20171217105138', 'allows the user to turn people''s memories into strips of film and manipulate them.', null, 'Charlotte Pudding', null, 1),

